var module = {};
module.exports = plotdb.chart.create({
  sample: function() {
    var list = d3.range(49);
    var continents = ["Asia","North America","South America","Africa","Europe","Australia","Oceania"];
    return {
      src: [{name: "Continent", data: list.map(function(d,i) { return continents[i % continents.length]; })}],
      des: [{name: "Continent", data: list.map(function(d,i) { return continents[parseInt(i / continents.length)]; })}],
      size: [{name: "Value", data: list.map(function(d,i) { return Math.round(100 * Math.random()); })}]
    };
  },
  dimension: {
    src: { type: [], require: true, desc: "name of source node" },
    des: { type: [], require: true, desc: "name of destination node" },
    size: { type: [plotdb.Number], require: true, desc: "flow size" }
  },
  config: {
    fontSize: {},
    margin: {},
    padding: {},
    palette: {},
    otherLimit: {rebindOnChange: true},
    otherLabel: {rebindOnChange: true},
    thick: { name: "Thickness", type: [plotdb.Number], default: 20 },
  },
  init: function() {
    var that = this, it;
    this.svg = d3.select(this.root).append("svg");
    this.srcGroup = this.svg.append("g").attr({class: "src arc"});
    this.desGroup = this.svg.append("g").attr({class: "des arc"});
    this.chordGroup = this.svg.append("g").attr({class: "chord connect arc"});
    setTimeout(function() {
      that.data.map(function(d,i) {
        d.size = Math.pow(d.size/10, 2) * 10;
        that.parse();
        that.resize();
        that.bind();
        that.render();
      });
    }, 1000);
    /*this.popupArc = plotd3.html.popup(this.root).on("mousemove", function(d,i,popup) {
      popup.select(".title").text(d.key);
      popup.select(".value").text(d.sum);
    });
    this.popupChord = plotd3.html.popup(this.root).on("mousemove", function(d,i,popup) {
      popup.select(".value:nth-of-type(1)").text(d.src);
      popup.select(".value:nth-of-type(2)").text("â†“ ( " + d.size +" )");
      popup.select(".value:nth-of-type(3)").text(d.des);
    });
    this.popupChord.getPopupNode().call(function(d,i) {
      this.select(".title").remove();
      this.append("div").attr({class: "value"});
      this.append("div").attr({class: "value"});
    });*/
  },
  parse: function() {
    var that = this;
    var dessize = {}, srcsize = {};
    if(!this.dimension.src.fields.length) this.data.map(function(d,i) { d.src = "?"; });
    if(!this.dimension.des.fields.length) this.data.map(function(d,i) { d.des = "?"; });
    if(!this.dimension.size.fields.length) this.data.map(function(d,i) { d.size = 1; });
    this.data = this.data.filter(function(it) { return it.src && it.des; });
    this.data.forEach(function(d,i) {
      dessize[d.des] = (dessize[d.des] || 0) + d.size;
      srcsize[d.src] = (srcsize[d.src] || 0) + d.size;
    });
    this.data.forEach(function(d,i) {
      if(dessize[d.des] < that.config.otherLimit) d.des = that.config.otherLabel;
      if(srcsize[d.src] < that.config.otherLimit) d.src = that.config.otherLabel;
    });
    dessize = {}, srcsize = {};
    this.data.forEach(function(d,i) {
      dessize[d.des] = (dessize[d.des] || 0) + d.size;
      srcsize[d.src] = (srcsize[d.src] || 0) + d.size;
    });
    var dsmap = {};
    var parsed = [];
    this.data.forEach(function(d,i) {
      var name = "[" + d.src +"][" + d.des + "]";
      if(!dsmap[name]) {
        dsmap[name] = {src: d.src, des: d.des, size: 0};
        parsed.push(dsmap[name]);
      }
      dsmap[name].size += d.size;
    });
    this.data = parsed;
    this.nameLen = d3.max(this.data.map(function(it) { return [it.src, it.des]; })
      .reduce(function(a,b) { return a.concat(b); }, [])
      .map(function(it) { return (it || "").length; }));
    var mapper = function(key) {
      var ret = d3.nest().key(function(it) { return it[key]; }).entries(that.data).map(function(it) {
        it.sum = it.values.reduce(function(a,b) { return a + b.size; }, 0);
        return it;
      });
      var total = ret.reduce(function(a,b) { return a + b.sum; }, 0);
      var offset = 0;
      ret.sort(function(a,b) { return b.sum - a.sum; });
      for(var i = 0; i < ret.length; i++) {
        it = ret[i];
        it.percent = it.sum / total;
        it.offset = offset;
        it._offset = 0;
        offset += it.percent;
        it.side = key;
      }
      return ret;
    };
    this.srcmap = mapper("src");
    this.desmap = mapper("des");
    this.data.sort(function(a,b) {
      var ret = dessize[b.des] - dessize[a.des];
      if(ret!=0) return ret;
      return srcsize[b.src] - srcsize[a.src];
    });
    for(var i = 0, src, des; i < this.data.length; i++) {
      it = this.data[i];
      src = this.srcmap.filter(function(v) { return v.key == it.src; })[0];
      des = this.desmap.filter(function(v) { return v.key == it.des; })[0];
      it.coord = {
        src: {node: src, offset: src._offset, percent: it.size / src.sum },
        des: {node: des, offset: des._offset, percent: it.size / des.sum }
      };
      src._offset += (it.size / src.sum);
      des._offset += (it.size / des.sum);
    }
  },
  bind: function() {
    var that = this,sel,n1,n2;
    sel = this.srcGroup.selectAll("path.arc.src").data(this.srcmap);
    sel.exit().remove();
    n1 = sel.enter().append("path").attr({class: "arc src"});
    sel = this.desGroup.selectAll("path.arc.des").data(this.desmap);
    sel.exit().remove();
    n2 = sel.enter().append("path").attr({class: "arc des"});
    sel = this.chordGroup.selectAll("path.chord").data(this.data);
    sel.exit().remove();
    sel.enter().append("path").attr({class: "chord"});
    sel = this.svg.selectAll("text.label.src").data(this.srcmap);
    sel.exit().remove();
    sel.enter().append("text").attr({class: "label src"});
    sel = this.svg.selectAll("text.label.des").data(this.desmap);
    sel.exit().remove();
    sel.enter().append("text").attr({class: "label des"});
    //this.popupChord.nodes(this.svg.selectAll("path.chord"));
    //this.popupArc.nodes(n1).nodes(n2);
    this.svg.selectAll("path.chord").on("mouseover", function(d,i) {
      if(that.handle) {
        clearTimeout(that.handle);
        that.handle = null;
      }
      that.target = {src: d.src, side: null, des: d.des};
      that.render();
    }).on("mouseout", function(d,i) {
      that.handle = setTimeout(function() {
        that.target = null; 
        that.render();
      }, 1000);
    });
    this.svg.selectAll("path.arc").on("mouseover", function(d,i) {
      if(that.handle) {
        clearTimeout(that.handle);
        that.handle = null;
      }
      that.target = {src: d.src || d.key, side: d.side, des: d.des || d.key};
      that.render();
    }).on("mouseout", function(d,i) {
      that.handle = setTimeout(function() {
        that.target = null; 
        that.render();
      }, 1000);
    });
  },
  resize: function() {
    var that = this;
    var box = this.root.getBoundingClientRect();
    var width = this.width = box.width;
    var height = this.height = box.height;
    var mw = width - this.config.fontSize * ( this.nameLen + 5);
    var mh = height - this.config.fontSize * 2;
    var size = this.size = (mw > mh ? mh : mw ) / 2
    this.svg.attr({
      width: width + "px", height: height + "px",
      viewBox: [0,0,width,height].join(" "),
      preserveAspectRatio: "xMidYMid"
    });
    //this.popupChord.fontSize(this.config.fontSize);
    //this.popupArc.fontSize(this.config.fontSize);
    this.thick = this.config.thick * ( this.width < 640 ? 0.5 : 1);
    this.arc = d3.svg.arc().innerRadius(size - this.thick).outerRadius(size);
    this.srcAscale = d3.scale.linear().domain([0,1]).range([-Math.PI/6,-Math.PI*5/6]);
    this.desAscale = d3.scale.linear().domain([0,1]).range([Math.PI/6,Math.PI*5/6]);
    this.color = d3.scale.ordinal().range(that.config.palette.colors.map(function(it) { return it.hex; }));
  },
  render: function() {
    var that = this,sel;
    sel = this.svg.selectAll("text.label");
    if(this.inited) sel = sel.transition("morph").duration(500);
    sel.attr({
      transform: function(d,i) {
        var scale = (d3.select(this).classed("src") ? that.srcAscale : that.desAscale);
        var a = ( scale(d.offset) + scale(d.offset + d.percent)) / 2;
        var x = Math.sin(a) * (that.size + that.config.fontSize);
        var y = -Math.cos(a) * (that.size + that.config.fontSize * 1.5);
        return [
          "translate(",
          x + that.width / 2,
          y + that.height / 2,
          ")"
        ].join(" ");
      },
      "text-anchor": function(d,i) {
        return d3.select(this).classed("src") ? "end" : "start";
      },
      "dominant-baseline": "central",
      dx: function(d,i) {
        return that.config.fontSize * (d3.select(this).classed("src") ? -0 : 0);
      },
      "font-size": that.config.fontSize
    }).text(function(d,i) { return d.key; });
    this.svg.selectAll("g.arc").attr({
      transform: "translate(" + (that.width /2) + "," + (that.height / 2) + ")"
    });
    sel = this.svg.selectAll("path.arc");
    if(this.inited) sel = sel.transition("morph").duration(500);
    sel.attr({
      d: function(d,i) {
        scale = (d3.select(this).classed("src") ? that.srcAscale : that.desAscale);
        return that.arc.startAngle(scale(d.offset)).endAngle(scale(d.offset + d.percent))();
      },
      fill: function(d,i) {
        return that.color(d.key);
      },
      stroke: "#fff",
      "stroke-width": 1
    });
    this.svg.selectAll("path.arc").transition("opacity").duration(500).attr({
      opacity: function(d,i) {
        var issrc = d3.select(this).classed("src");
        return (!that.target || (
          (issrc && that.target.side == "src" && d.key == that.target.src) || 
          (!issrc && that.target.side == "des" && d.key == that.target.des) ||
          (that.target.side == null && (
            (d.key == that.target.src && issrc) ||
            (d.key == that.target.des && !issrc)
          ))
        ) ? 1 : 0.2);
      },
    });
    getp = function(p) {
      return [Math.sin(p) * (that.size - that.thick),
        -Math.cos(p) * (that.size - that.thick)];
    };
    getc = function(p, q, r) {
      if(typeof(r) == "undefined") r = 3;
      return ["C", p[0]/r, p[1]/r, q[0]/r, q[1]/r, q[0], q[1]];
    }
    sel = this.svg.selectAll("path.chord");
    if(this.inited) sel = sel.transition("morph").duration(500);
    sel.attr({
      d: function(d,i) {
        var src = d.coord.src, des = d.coord.des;
        var src1 = that.srcAscale(src.node.offset + src.node.percent * src.offset);
        var src2 = that.srcAscale(src.node.offset + src.node.percent * (src.offset + src.percent));
        var des1 = that.desAscale(des.node.offset + des.node.percent * des.offset);
        var des2 = that.desAscale(des.node.offset + des.node.percent * (des.offset + des.percent));
        var c1 = 0, c2 = 0;
        var p1 = getp(src1), p2 = getp(src2), p3 = getp(des1), p4 = getp(des2);
        var s = that.size - that.thick;
        return [
          ["M", p1[0], p1[1]],
          ["A", s, s, 0, 0, 0, p2[0], p2[1]],
          getc(p2, p4),
          ["A", s, s, 0, 0, 0, p3[0], p3[1]],
          getc(p3, p1)
        ].reduce(function(a,b) { return a.concat(b); }, []).join(" ");
      },
      fill: function(d,i) {
        return that.color(d.coord.des.node.key);
      },
      stroke: "#fff",
      "stroke-width": 1
    });
    this.svg.selectAll("path.chord").transition("opacity").duration(500).attr({
      opacity: function(d,i) {
        if(!that.target) return 0.5;
        if(that.target && (
          (that.target.side == "src" && that.target.src == d.src) ||
          (that.target.side == "des" && that.target.des == d.des) ||
          (that.target.side == null && that.target.src == d.src && that.target.des == d.des)
        )) return 1;
        else return 0.1;
      }
    });
    this.inited = true;
  }
});

var module = {};
module.exports = plotdb.chart.create({
  sample: function() {
    var list = d3.range(10);
    return {
      value1: [{name: "Value1", data: list.map(function(d,i) { return Math.round(100*Math.random()); })}],
      value2: [{name: "Value2", data: list.map(function(d,i) { return Math.round(100*Math.random()); })}],
      value3: [{name: "Value3", data: list.map(function(d,i) { return Math.round(100*Math.random()); })}],
    };
  },
  dimension: {
    value1: { type: [plotdb.Number], require: true, desc: "" },
    value2: { type: [plotdb.Number], require: true, desc: "" },
    value3: { type: [plotdb.Number], require: true, desc: "" }
  },
  config: {
    margin: {},
    fontSize: {},
    gridStroke: {},
    palette: {default: {colors:[{hex:"#f00"},{hex:"#0f0"},{hex:"#00f"}]}},
    value1Label: { name: "Value 1 Label", extend: "xAxisLabel" },
    value2Label: { name: "Value 2 Label", extend: "xAxisLabel" },
    value3Label: { name: "Value 3 Label", extend: "xAxisLabel" },
  },
  init: function() {
    var that = this;
    this.svg = d3.select(this.root).append("svg");
    this.gridGroup = this.svg.append("g").attr({class: "grid-group"});
    this.dataGroup = this.svg.append("g").attr({class: "data-group"});
    this.frame = this.gridGroup.append("path").attr({class: "frame"});
    this.tooltip = plotd3.html.tooltip(this.root).on("mousemove", function(d,i,popup) {
      popup.select(".value").text([d.value1,d.value2,d.value3].join(" / "));
    });
    this.gridGroup.append("text").attr({class: "value1"});
    this.gridGroup.append("text").attr({class: "value2"});
    this.gridGroup.append("text").attr({class: "value3"});
  },
  calc: function(v) {
    var SQRT32 = Math.sqrt(3)/2;
    return [
      0.5 * (2 * v[1] + v[2]) / (v[0] + v[1] + v[2]),
      SQRT32 * v[2] / (v[0] + v[1] + v[2])
    ];
  },
  parse: function() {
    var that = this;
    var SQRT32 = Math.sqrt(3)/2;
    this.data.map(function(d,i) {
      if(!d.value1) d.value1 = 0;
      if(!d.value2) d.value2 = 0;
      if(!d.value3) d.value3 = 0;
      d.sum = d.value1 + d.value2 + d.value3;
      if(d.sum==0) d.value1 = d.sum = 1;
      d.values = [d.value1, d.value2, d.value3].map(function(v,i) { return v/d.sum; });
      d.coord = that.calc(d.values);
    });
  },
  bind: function() {
    var that = this,sel;
    sel = this.dataGroup.selectAll("circle.data").data(this.data);
    sel.exit().remove();
    sel = sel.enter().append("circle").attr({class: "data"});
    this.tooltip.nodes(sel);
    this.ticks = d3.range(4).map(function(d,i) { return (d/5) + (1/5); });
    ["value1","value2","value3"].map(function(name) {
      sel = that.gridGroup.selectAll("line.grid." + name).data(that.ticks);
      sel.exit().remove();
      sel.enter().append("line").attr({class: "grid " + name});
    });
  },
  resize: function() {
    var that = this;
    var box = this.root.getBoundingClientRect();
    var width = this.width = box.width;
    var height = this.height = box.height;
    var SQRT32 = Math.sqrt(3)/2;
    if(width > height - this.config.fontSize * 4) {
      this.size = (height - this.config.margin * 2 - this.config.fontSize * 4) / SQRT32;
    } else {
      this.size = (width - this.config.margin * 2);
    }
    this.xMin = (this.width - this.size)/2;
    this.xMax = (this.width + this.size)/2;
    this.yMin = (this.height - this.size * SQRT32)/2;
    this.yMax = (this.height + this.size * SQRT32)/2;
    this.tooltip.fontSize(this.config.fontSize);
    this.svg.attr({
      width: width + "px", height: height + "px",
      viewBox: [0,0,width,height].join(" "),
      preserveAspectRatio: "xMidYMid"
    });
    this.xScale = d3.scale.linear()
      .domain([0,1])
      .range([this.xMin, this.xMax]);
    this.yScale = d3.scale.linear()
      .domain([0,SQRT32])
      .range([this.yMax, this.yMin]);
  },
  render: function() {
    var that = this,sel;
    var SQRT32 = Math.sqrt(3)/2;
    var c1 = d3.rgb((this.config.palette.colors[0] || {}).hex || "#f00");
    var c2 = d3.rgb((this.config.palette.colors[1] || {}).hex || "#0f0");
    var c3 = d3.rgb((this.config.palette.colors[2] || {}).hex || "#00f");
    this.frame.attr({
      d: [
        "M", this.xMin, this.yMax,
        "L", this.xMax, this.yMax,
        "L", (this.width/2), this.yMin,
        "Z"
      ].join(" "),
      fill: "none",
      stroke: "black",
      "stroke-width": 2
    });
    sel = this.dataGroup;
    if(this.inited) sel = sel.transition("morph").duration(500);
    sel.selectAll("circle.data").attr({
      cx: function(d,i) { return that.xScale(d.coord[0]); },
      cy: function(d,i) { return that.yScale(d.coord[1]); },
      r: 4,
      fill: "white",
      stroke: function(d,i) {
        var c = ["r","g","b"].map(function(v,i) {
          return parseInt(c1[v] * d.values[i] + c2[v] * d.values[i] + c3[v] * d.values[i]);
        });
        return d3.rgb(c[0], c[1], c[2]).toString()
      },
      "stroke-width": 2
    });
    ["value1","value2","value3"].map(function(name,i) {
      that.gridGroup.selectAll("line.grid."+name).each(function(d) {
        var p1 = [1,0], p2 = [0,1],v = (d/(1-d));
        p1.splice(i,0,v);
        p2.splice(i,0,v);
        p1 = that.calc(p1);
        p2 = that.calc(p2);
        d3.select(this).attr({
          x1: that.xScale(p1[0]), x2: that.xScale(p2[0]),
          y1: that.yScale(p1[1]), y2: that.yScale(p2[1]),
          stroke: that.config.gridStroke,
          "stroke-width": 1
        });
      });
    });
    this.gridGroup.selectAll("text").attr("font-size", this.config.fontSize);
    this.gridGroup.select("text.value1").attr({
      x: this.xMin, y: this.yMax, dy: this.config.fontSize * 1.3,
      fill: c1.darker().toString()
    }).text(this.config.value1Label || this.dimension.value1.fieldName || " ");
    this.gridGroup.select("text.value2").attr({
      x: this.xMax, y: this.yMax, dy: this.config.fontSize * 1.3, "text-anchor": "end",
      fill: c2.darker().toString()
    }).text(this.config.value2Label || this.dimension.value2.fieldName || " ");
    this.gridGroup.select("text.value3").attr({
      x: this.width/2, y: this.yMin, dy: -this.config.fontSize * 0.5, "text-anchor": "middle",
      fill: c3.darker().toString()
    }).text(this.config.value3Label || this.dimension.value3.fieldName || " ");
    this.inited = true;
  }
});

var module = {};
module.exports = plotdb.chart.create({
  sample: function() {
    var list = d3.range(20);
    return {
      value: [{name: "Score", data: list.map(function(d,i) { return Math.round(Math.random() * 90 + 10); })}],
      name: [{name: "Name", data: list.map(function(d,i) { return plotdb.data.sample.name.rand(); })}],
      category: [{name: "Dept", data: list.map(function(d,i) { return plotdb.data.sample.category.rand(); })}]
    };
  },
  dimension: {
    value: { type: [plotdb.Number], require: true, desc: "size of the block" },
    name: { type: [], require: false, desc: "name of the block" },
    category: { type: [], require: false, desc: "parent of the block"}
  },
  config: {
    fontSize: {},
    margin: {},
    palette: {},
    legendLabel: {},
    labelShadowSize: {},
  },
  init: function() {
    var that = this;
    this.svg = d3.select(this.root).append("svg");
    this.legendGroup = this.svg.append("g").attr({class: "legend-group"});
    setInterval(function() {
      that.tick();
    }, 30);
  },
  parse: function() {
    this.data.map(function(it) { 
      it.key = it.name;
      it.data = it.value;
    });
    this.categories = d3.map(this.data, function(d,i) { return d.category; }).keys();
    this.parsed = {children: d3.nest().key(function(it) { return it.category; }).entries(this.data).map(function(it) {
      it.children = it.values;
      return it;
    })};
  },
  bind: function() {
    var that = this, sel;
    var sites = this.treemap.getSites();
    sel = this.svg.selectAll("path.data").data(this.treemap.getPolygons());
    sel.exit().remove();
    sel.enter().append("path").attr({"class": "data", opacity: 1});
    this.svg.selectAll("path.data").attr({
      class: function(d,i) {
        return (sites[i].lv <=0 ? "data group" : "data");
      }
    });
    this.polygons = this.svg.selectAll("path.data");
    sel = this.svg.selectAll("g.label").data(sites);
    sel.exit().remove();
    sel.enter().append("g").attr({"class": "label"}).each(function(it) {
      var node = d3.select(this);
      node.append("text").attr({"class": "name shadow"});
      node.append("text").attr({"class": "value shadow"});
      node.append("text").attr({"class": "name"});
      node.append("text").attr({"class": "value"});
    });
    this.sites = this.svg.selectAll("g.label");
  },
  resize: function() {
    var that = this;
    var box = this.root.getBoundingClientRect();
    var width = this.width = box.width;
    var height = this.height = box.height;
    this.svg.attr({
      width: width + "px", height: height + "px",
      viewBox: [0,0,width,height].join(" "),
      preserveAspectRatio: "xMidYMid"
    });
    this.cScale = plotdb.Palette.scale.ordinal(this.config.palette);
    this.legend = plotd3.rwd.legend()
      .scale(this.cScale)
      .orient("bottom")
      .tickValues(this.categories)
      .size([this.width - 2 * this.config.margin, 100])
      .fontSize(this.config.fontSize);
    this.legend.label(this.config.legendLabel || this.dimension.value.fieldName || "");
    this.legendGroup.call(this.legend).selectAll(".legend").on("mouseover", function(d,i) {
      var sites = that.treemap.getSites();
      that.activeGroup = d;
      that.polygons.transition().attr({
        opacity: function(d,i) {
          var data = sites[i];
          return (!that.activeGroup || that.activeGroup == data.key || that.activeGroup == data.category ? 1 : 0.1);
        }
      });
    }).on("mouseout", function(d,i) {
      that.activeGroup = null;
      that.polygons.transition().attr({ opacity: 1 });
    });
    this.legendSize = this.legend.offset();
    this.xScale = d3.scale.linear().domain([0,width]).range([
      this.config.margin, 
      width - this.config.margin
    ]);
    this.yScale = d3.scale.linear().domain([0,height]).range([
      this.config.margin, 
      height - this.config.margin - this.legendSize[1] - this.config.fontSize
    ]);
    this.treemap = new Voronoi.Treemap(this.parsed, Voronoi.Polygon.create(width, height, 60), width, height);
  },
  tick: function() {
    if(!this.treemap) return ;this.treemap.compute();
    this.render();
  },
  render: function() {
    var that = this;
    var sites = this.treemap.getSites();
    var polygons = this.treemap.getPolygons();
    this.legendGroup.attr({
      transform: ["translate(", (this.width - this.legendSize[0])/2, (this.height - this.legendSize[1] - this.config.margin), ")"].join(" ")
    });
    this.polygons.data(polygons).attr({
      d: function(it) {
        if(!it || !it.length) return ""
        ret = ["M", that.xScale(it[0].x), that.yScale(it[0].y)];
        for(var idx = 0; idx < it.length; idx++) {
          ret.push("L");
          ret.push(that.xScale(it[idx].x));
          ret.push(that.yScale(it[idx].y));
        }
        ret.push("L");
        ret.push(that.xScale(it[0].x));
        ret.push(that.yScale(it[0].y));
        return ret.join(" ");
      },
      fill: function(it,i) {
        if(sites[i].lv == 0) return that.cScale(sites[i].key);
        else return "none"
      },
      stroke: "#000",
      "stroke-width": function(it,i) {
        if(sites[i].lv <= 0) return 5;
        return 1;
      }
    });
    this.sites.attr({
      transform: function(it) {
        var x = that.xScale(it.x);
        var y = that.yScale(it.y);
        if(isNaN(x)) x = -10000;
        if(isNaN(y)) y = -10000;
        return ["translate(", x, y, ")"].join(" ");
      },
      "font-size": that.config.fontSize,
    }).each(function(it) {
      var node = d3.select(this);
      attr = {
        fill: function(it) {
          var hsl = d3.hsl(that.cScale(it.category));
          if(hsl.l > 0.65) return "#000";
          return "#fff";
        },
        "text-anchor": "middle",
        "dominant-baseline": "central"
      };
      node.selectAll("text.name").attr(attr).attr("dy", -that.config.fontSize/2).text(function(it) {
        if(it.lv >= 1) return it.name;
        return "";
      });
      node.selectAll("text.value").attr(attr).attr({
        dy: that.config.fontSize/2,
        "font-size": "0.8em",
        "font-weight": "200"
      }).text(function(it) {
        if(it.lv >= 1) return Math.round(100*it.data)/100;
        return "";
      });
      node.selectAll("text.shadow").attr({
        stroke: function(it) {
          var hsl = d3.hsl(that.cScale(it.category));
          if(hsl.l > 0.65) return "#fff";
          return "#000";
        },
        "stroke-width": that.config.labelShadowSize
      });
    });
  }
});

