    var value = [{name: "value", data: d3.range(count).map(function() { 
      var v = Math.random();
      if(v < 0.7) return Math.random() + 1;
      if(v < 0.9) return Math.random() * 5 + 3;
      return Math.random() * 10 + 6;
      return Math.random(); 
    })}];
    var name = [{name: "name", data: d3.range(count).map(function() { return plotdb.data.sample.name.rand(); })}];
    var category = [{name: "category", data: d3.range(count).map(function() { return plotdb.data.sample.category.rand(); })}];
    return {
      value: value,
      name: name,
      category: category
    };
  },

  dimension: {
    value: { type: [plotdb.Number], require: true, desc: "size of circle" },
    name: { type: [], require: false, desc: "tag of circle" },
    category: { type: [], require: false, desc: "category of circle"},
  },
  config: {
    fontFamily: {},
    background: {},
    fontSize: {},
    margin: {},
    bubblePadding: {},
    palette: {},
    legendShow: {},
    labelShadowSize: {},
    labelShow: {},
    sort: { type: [plotdb.Choice(["value","category","name"])], default: "value", rebindOnChange: true},
    catInBubble: { name: "Show Category", type: [plotdb.Boolean], default: true, category: "Special Settings"  },
  },
  init: function() {
    var that = this;
    this.svg = d3.select(this.root).append("svg");
    this.legendGroup = this.svg.append("g").attr({class: "legend-group"});
    this.popup = plotd3.html.popup(this.root).on("mousemove", function(d,i,popup) {
      popup.select(".title").text(d.name);
      popup.select(".value").text(parseInt(100 * d.value)/100);
    });
  },
  parse: function() {
    if(this.dimension.category.fields.length) {
      this.categories = d3.map(this.data, function(d,i) { return d.category; }).keys();
    } else {
      this.categories = [];
    }
  },
  bind: function() {
    var that = this,sel;
    this.resize();
    sel = this.svg.selectAll("circle").data(this.nodes);
    sel.exit().remove();
    sel = sel.enter().append("circle").attr({opacity: 1});
    this.popup.nodes(sel);
    sel = this.svg.selectAll("g.label").data(this.nodes);
    sel.exit().remove();
    sel = sel.enter().append("g").attr({class: "label"}).each(function(d,i) {
      var node = d3.select(this);
      node.append("text").attr({class: "name shadow"});
      node.append("text").attr({class: "category shadow"});
      node.append("text").attr({class: "name"});
      node.append("text").attr({class: "category"});
    });
