        dy: function() {
          if(!that.config.catInBubble) return 0;
          return ( d3.select(this).classed("name") ? -1 : 1 ) * (d.f1 + d.f2) / 4;
        },
        "font-size": function() {
          return d3.select(this).classed("name") ? d.f1 : d.f2;
        },
        "font-weight": function() {
          return d3.select(this).classed("name") ? "bold" : "light";
        },
        opacity: function() {
          if(!that.config.labelShow) return 0;
          if(!that.config.catInBubble && d3.select(this).classed("category")) return 0;
          return 1;
        },
        fill: function() {
          var hsl = d3.hsl(that.cScale(d.category));
          if(hsl.l > 0.65) return "#000"; else return "#fff";
        },
        stroke: function(it) {
          if(!d3.select(this).classed("shadow")) return "none";
          var hsl = d3.hsl(that.cScale(it.category));
          if(hsl.l > 0.65) return "#fff"; else return "#000";
        },
        "stroke-width": function(d,i) {
          if(!d3.select(this).classed("shadow")) return 2;
          return that.config.labelShadowSize || 0;
        },
      }).text(function() { return d3.select(this).classed("name") ? d.name : d.category; });
    });
    this.labels.attr({
      opacity: function(d,i) {
        var node = d3.select(this);
        var wName = node.select("text.name")[0][0].getBBox().width;
        var wCat = node.select("text.category")[0][0].getBBox().width;
        if(wName >= d.r * 2.2 || wCat >= d.r * 2.2) return 0;
        return 1;
      }
    });
    this.inited = true;
  }
});
